import { Link } from 'react-router-dom'; 
import './All.css';
import React, {useEffect, useState } from "react";
import Axios from "axios";

function All() {

    const [data, setUserList] = useState([]);
   
    
      const getuser = async () =>
      {
        try{
            const response = await Axios.get("http://localhost:3002/details");
            return response.data.users ;
        }catch(error)
        {
            console.error("fetching err" ,error);
        }
      };
    
      useEffect(() => {
        getuser().then(setUserList);
      }, []);
      getuser();

      const handlDelete =async(id)=>
      {
        console.log("methana hari");
        try
        {
         Axios.delete(`http://localhost:3002/api/deleteuser${id}`)
          .then((response) => {
            console.log("User deleted");
            getUser();
          });
        }catch(error)
        {
          console.log("fetching err" ,error);
        }
      
      }

    return (
        <div className="app-container">
            <div className="outerbox">
                <div className="sidebar">
                    <ul>
                        <li><Link to="/">Add</Link></li>
                        <hr className="sidebar-line" />
                        <li><a href="#update">Update</a></li>
                        <hr className="sidebar-line" />
                        <li><Link to="/">All</Link></li>
                    </ul>
                </div>
              
            </div>
            <div>
<div className='tablebox' >
      <table border="1" style={{ width: "100%", margin: "auto", textAlign: "left" }}>
        <thead>
          <tr>
     
            <th>disticts</th>
            <th>stream</th>
            <th>course</th>
            <th>Z-Range</th>
           
            <th>action</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row) => (
            <tr key={row.id}>
           
              <td>{row.district}</td>
              <td>{row.stream}</td>
              <td>{row.course}</td>
              <td>{row.zValue1} - {row.zValue2}</td>
              
              <td>
                <button >Update</button>
                <button onClick={() =>handlDelete(row._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      </div>
    </div>
</div>
    );
}

export default All;
