import React from 'react';
import { Link } from 'react-router-dom'; 
import './All.css';

function All() {


    const [data, setData] = useState([
        { id: 1, name: "John", age: 25, email: "john@example.com", city: "New York" },
        { id: 2, name: "Jane", age: 30, email: "jane@example.com", city: "Los Angeles" },
        { id: 3, name: "Mike", age: 28, email: "mike@example.com", city: "Chicago" },
      ]);
    
      // Handle delete
      const handleDelete = (id) => {
        const updatedData = data.filter((item) => item.id !== id);
        setData(updatedData);
      };
    
      // Handle update (for simplicity, just logs the row data)
      const handleUpdate = (row) => {
        console.log("Update row:", row);
        alert(`Update row with ID: ${row.id}`);
      };


    return (
        <div className="app-container">
            <div className="outerbox">
                <div className="sidebar">
                    <ul>
                        <li><Link to="/">Add</Link></li>
                        <hr className="sidebar-line" />
                        <li><a href="#update">Update</a></li>
                        <hr className="sidebar-line" />
                        <li><Link to="/">All</Link></li>
                    </ul>
                </div>
              
            </div>
            <div>
      <h1>Simple Table</h1>
      <table border="1" style={{ width: "80%", margin: "auto", textAlign: "left" }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Age</th>
            <th>Email</th>
            <th>City</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row) => (
            <tr key={row.id}>
              <td>{row.id}</td>
              <td>{row.name}</td>
              <td>{row.age}</td>
              <td>{row.email}</td>
              <td>{row.city}</td>
              <td>
                <button onClick={() => handleUpdate(row)}>Update</button>{" "}
                <button onClick={() => handleDelete(row.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
        </div>
    );
}

export default All;
