import { Link } from 'react-router-dom'; 
import './All.css';
import React, { useEffect, useState } from "react";
import Axios from "axios";

function All() {
  const [data, setUserList] = useState([]);

  // Function to fetch user data
  const getuser = async () => {
    try {
      const response = await Axios.get("http://localhost:3002/details");
      setUserList(response.data.users); // Update state directly
    } catch (error) {
      console.error("Error fetching user data:", error);
    }
  };

  // Fetch user data on component mount
  useEffect(() => {
    getuser();
  }, []);

  // Function to handle deletion
  const handlDelete = async (id) => {
    console.log("Delete request initiated for ID:", id);
    try {
      await Axios.delete(`http://localhost:3002/api/deleteuser/${id}`);
      console.log("User deleted successfully");
      getuser(); // Refresh the data in the table
    } catch (error) {
      console.error("Error during delete request:", error);
    }
  };

  return (
    <div className="app-container">
      <div className="outerbox">
        <div className="sidebar">
          <ul>
            <li><Link to="/">Add</Link></li>
            <hr className="sidebar-line" />
            <li><a href="#update">Update</a></li>
            <hr className="sidebar-line" />
            <li><Link to="/">All</Link></li>
          </ul>
        </div>
      </div>
      <div>
        <div className="tablebox">
          <table border="1" style={{ width: "100%", margin: "auto", textAlign: "left" }}>
            <thead>
              <tr>
                <th>Districts</th>
                <th>Stream</th>
                <th>Course</th>
                <th>Z-Range</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {data.map((row) => (
                <tr key={row.id}>
                  <td>{row.district}</td>
                  <td>{row.stream}</td>
                  <td>{row.course}</td>
                  <td>{row.zValue1} - {row.zValue2}</td>
                  <td>
                    <button>Update</button>
                    <button onClick={() => handlDelete(row._id)}>Delete</button> {/* Corrected onClick */}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default All;
