import React, { useState } from "react";
import "./feedback.css";
import Axios from "axios";

const FeedbackPage = () => {
  const [formData, setDetails] = useState({
    name: "",
    email: "",
    message: "",
  });
  const [submitted, setSubmitted] = useState(false);
  const [users, setUsers] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Add login state
  const [notification, setNotification] = useState(""); // Add notification state

  const handleInput = (event) => {
    const { id, value } = event.target;
    setDetails((prevFormData) => ({
      ...prevFormData,
      [id]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!isLoggedIn) {
      // Check if the user is logged in
      setNotification("Please log in to submit feedback.");
      return;
    }

    const newdata = {
      id: Date.now(),
      name: formData.name,
      message: formData.message,
      email: formData.email,
    };

    setUsers((prevUsers) => [...prevUsers, newdata]);
    addData(newdata);
    setSubmitted(true);
  };

  const addData = (newdata) => {
    console.log(newdata);
    Axios.post("http://localhost:3002/feedback", newdata)
      .then((response) => {
        console.log("User added:", response.data);
      })
      .catch((error) => {
        console.error("Error adding user:", error);
      });
  };

  const handleLogin = () => {
    // Mock login functionality
    setIsLoggedIn(true);
    setNotification(""); // Clear any existing notifications
  };

  return (
    <div className="fdouter">
      <div className="feedback-container">
        <h1>Feedback</h1>
        {!isLoggedIn && (
          <div className="login-notification">
            <p>{notification}</p>
            <button onClick={handleLogin}>Log in</button>
          </div>
        )}
        {submitted ? (
          <div className="thank-you-message">
            <h2>Thank you for your feedback!</h2>
            <p>We appreciate your input and will get back to you if necessary.</p>
          </div>
        ) : (
          <form className="feedback-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="name">Name:</label>
              <input
                type="text"
                id="name"
                value={formData.name}
                onChange={handleInput}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email:</label>
              <input
                type="email"
                id="email"
                value={formData.email}
                onChange={handleInput}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="message">Message:</label>
              <textarea
                id="message"
                value={formData.message}
                onChange={handleInput}
                required
              ></textarea>
            </div>
            <button type="submit">Submit</button>
          </form>
        )}
      </div>
    </div>
  );
};

export default FeedbackPage;
